import React from 'react';
import { useSpring, animated } from 'react-spring';
import { FaEnvelope, FaTwitter, FaGithub, FaLinkedin } from 'react-icons/fa';
import './contact_styles.css';

const Contact = () => {
  const animationProps = useSpring({
    from: { opacity: 0, transform: 'translateY(50px)' },
    to: { opacity: 1, transform: 'translateY(0)' },
  });
  return (
    <div className="page">
      <div className="contact-content">
        <h2>Contact Me</h2>
        <div className="contact-details">
          <animated.div style={animationProps}>
          <div className="contact-item">
            <h2><FaEnvelope className="contact-icon" />Email:</h2>
            <p>shivanirudh2001@gmail.com</p>
          </div>
          </animated.div>
          <animated.div style={animationProps}>
          <div className="contact-item">
            <h2><FaGithub className="contact-icon" />Github:</h2>
            <p>+<a href="https://github.com/Shivanirudh" target="_blank" rel="noopener noreferrer">github.com/Shivanirudh</a></p>
          </div>
          </animated.div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
