import React from 'react';
import { useSpring, animated } from 'react-spring';
import { FaEnvelope, FaPhone } from 'react-icons/fa';
import './contact_styles.css';

const Contact = () => {
  const itemProps = useSpring({ opacity: 0, transform: 'translateY(50px)' });

  const handleMouseEnter = (props) => {
    props.set({ opacity: 1, transform: 'translateY(0)' });
  };

  const handleMouseLeave = (props) => {
    props.set({ opacity: 0, transform: 'translateY(50px)' });
  };
  return (
    <div className="page">
      <div className="contact-content">
        <h2>Contact Me</h2>
        <div className="contact-details">
          <animated.div className="contact-item"
            onMouseEnter={() => handleMouseEnter(itemProps)}
            onMouseLeave={() => handleMouseLeave(itemProps)}
            style={itemProps}>
          <div className="contact-item">
            <FaEnvelope className="contact-icon" />
            <p>Email: shivanirudh2001@gmail.com</p>
          </div>
          </animated.div>
          <animated.div className="contact-item"
            onMouseEnter={() => handleMouseEnter(itemProps)}
            onMouseLeave={() => handleMouseLeave(itemProps)}
            style={itemProps}>
          <div className="contact-item">
            <FaPhone className="contact-icon" />
            <p>Phone: +91 94454 35407</p>
          </div>
          </animated.div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
